fun main(args: Array<String>) {
    
    // Kotlin does not support ternary operators

    // Kotlin supports if statements written on one line

    val trueValue = true
    val falseValue = false

    val trueIfResult = if (trueValue) "trueResult" else "falseResult"
    // "trueResult"

    val falseIfResult = if (falseValue) "trueResult" else "falseResult"
    // "falseResult"

}