true_value = true
false_value = false

true_ternary_result = true_value ? "true_result" : "false_result"
# "true_result"

false_ternary_result = false_value ? "true_result" : "false_result"
# "false_result"

# Ruby supports if statements written on one line

true_if_result = if true_value then "true_result" else "false_result" end
# "true_result"

false_if_result = if false_value then "true_result" else "false_result" end
# "false_result"
